import java.util.*;
public class leetcode_395 {
	 public static int longestSubstring(String s, int k) {
	        if(k==1)
	            return s.length();
	        int max = 0;
	        for(int i=0 ; i<s.length() ; i++){
	            int left = 0;
	            HashMap<Character, Integer> set = new HashMap<>();
	            for(int j=i; j <s.length()-max; j++){
	                char x = s.charAt(j);
	                if(set.containsKey(x)){
	                    int occ = set.get(x);
	                    if(occ==k-1)
	                        left--;
	                    set.put(x, occ+1);
	                }else{
	                    left++;
	                    set.put(x, 1);
	                }
	                if(left==0)
	                    max = Math.max(max, j-i+1);
	            }
	        }
	        return max;
	    }
	public static void main(String[] args) {
		// TODO Auto-generated method stub
System.out.println(longestSubstring("", 301));
	}

}
